import './index.css';
import React, { useState, useEffect } from 'react';
import { ChevronRight, ChevronLeft, ChevronDown, Users, BookOpen, PieChart, DollarSign, Globe, School, Video } from 'lucide-react';
const importAll = (r) => r.keys().map(r);
const images = importAll(require.context('./images', false, /\.(png|jpe?g|svg)$/));
console.log('Imported images:', images);

const Slideshow = ({ images }) => {
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 5000);
    return () => clearInterval(timer);
  }, [images.length]);

  return (
    <div className="relative w-full h-96">
      {images.map((image, index) => (
        <div
          key={index}
          className={`absolute top-0 left-0 w-full h-full transition-opacity duration-1000 ${
            index === currentIndex ? 'opacity-100' : 'opacity-0'
          }`}
        >
         <img src={image} alt={`Slide ${index + 1}`} className="w-full h-full object-cover" />
        </div>
      ))}
      <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 flex space-x-2">
        {images.map((_, index) => (
          <button
            key={index}
            className={`w-3 h-3 rounded-full ${
              index === currentIndex ? 'bg-orange-500' : 'bg-orange-200'
            }`}
            onClick={() => setCurrentIndex(index)}
          />
        ))}
      </div>
    </div>
  );
};

// const LandingPage = () => {
//   // const slideshowImages = [
//   //   "/api/placeholder/1200/800",
//   //   "/api/placeholder/1200/800",
//   //   "/api/placeholder/1200/800",
//   // ];

//   const slideshowImages = images.filter(img => 
//     ['chair.jpg', 'incinerator.jpg', 'solar fix.jpg'].includes(img.default.split('/').pop())
//   );
const LandingPage = () => {
  const slideshowImages = images.filter(img => {
    if (typeof img === 'string') {
      return ['chair.jpg', 'incinerator.jpg', 'solar fix.jpg'].includes(img.split('/').pop());
    }
    return false;
  });
  return (
    <div className="min-h-screen bg-orange-50">
      {/* Hero Section with Slideshow */}
      <header className="relative text-white">
        <Slideshow images={slideshowImages} />
        <div className="absolute inset-0 bg-black bg-opacity-50 flex items-center justify-center">
          <div className="text-center">
            <h1 className="text-5xl font-bold mb-4">Empowering STEM Education Through Real-World Problem Solving</h1>
            <p className="text-xl mb-8">Join us in transforming education and creating the next generation of innovators.</p>
            <button className="bg-orange-500 hover:bg-orange-600 text-white font-bold py-3 px-6 rounded-full inline-flex items-center text-lg transition duration-300">
              Get Involved <ChevronRight className="ml-2" />
            </button>
          </div>
        </div>
        <div className="absolute bottom-0 left-0 right-0">
          <svg className="w-full h-16 text-orange-50" viewBox="0 0 1440 48" fill="currentColor" xmlns="http://www.w3.org/2000/svg">
            <path d="M0 48h1440V0c-283 0-424.5 48-720 48C424.5 48 283 0 0 0v48z"/>
          </svg>
        </div>
      </header>

      {/* Serwaa Nyarko Success Story */}
      <section className="py-20 bg-white">
        <div className="container mx-auto px-4">
          <h2 className="text-4xl font-bold mb-12 text-center text-orange-800">Our Success at Serwaa Nyarko Girls' School</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-12 items-center">
            <div>
              <p className="text-lg mb-6 text-orange-900">We implemented our hands-on STEM education model at Serwaa Nyarko Girls' School, leading to remarkable outcomes. Students developed innovative solutions to real-world problems, including:</p>
              <ul className="list-none mb-6 space-y-2">
                {["Waste segregation bins", "Biogas systems", "Adjustable tables and chairs from plastic waste", "Eco-friendly sanitary pad incinerators", "Solar-powered intruder detection systems"].map((item, index) => (
                  <li key={index} className="flex items-center text-orange-700">
                    <ChevronRight className="mr-2 text-orange-500" />
                    {item}
                  </li>
                ))}
              </ul>
              <p className="text-lg text-orange-900">These projects not only enhanced the students' STEM skills but also had a direct positive impact on their school environment.</p>
            </div>
            <div className="grid grid-cols-2 gap-4">
              <img src="/api/placeholder/300/200" alt="Students working on projects" className="rounded-lg shadow-md transform hover:scale-105 transition duration-300" />
              <img src="/api/placeholder/300/200" alt="Completed student project" className="rounded-lg shadow-md transform hover:scale-105 transition duration-300" />
              <img src="/api/placeholder/300/200" alt="Students presenting their work" className="rounded-lg shadow-md transform hover:scale-105 transition duration-300" />
              <div className="relative group">
                <img src="/api/placeholder/300/200" alt="Video thumbnail" className="rounded-lg shadow-md transform group-hover:scale-105 transition duration-300" />
                <div className="absolute inset-0 flex items-center justify-center">
                  <Video className="text-white h-12 w-12 group-hover:text-orange-500 transition duration-300" />
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Support Your School Section */}
      <section className="py-20 bg-orange-100">
        <div className="container mx-auto px-4">
          <h2 className="text-4xl font-bold mb-12 text-center text-orange-800">Support Your School</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-12">
            {/* SHS Support Options */}
            <div className="bg-white p-8 rounded-lg shadow-lg">
              <h3 className="text-3xl font-bold mb-6 text-center text-orange-700">Senior High School (SHS)</h3>
              <p className="mb-6 text-center text-lg text-orange-600">Old students and parents can support SHS projects:</p>
              <div className="grid grid-cols-2 gap-6">
                {[
                  { amount: "GHC 15,000", projects: 1 },
                  { amount: "GHC 25,000", projects: 2 },
                  { amount: "GHC 40,000", projects: 3 },
                  { amount: "GHC 50,000", projects: 5 },
                ].map((tier, index) => (
                  <div key={index} className="bg-orange-50 p-6 rounded-lg text-center transform hover:scale-105 transition duration-300">
                    <h4 className="text-2xl font-semibold mb-2 text-orange-700">{tier.amount}</h4>
                    <p className="mb-4 text-orange-600">for {tier.projects} project{tier.projects > 1 ? 's' : ''}</p>
                    <button className="bg-orange-500 hover:bg-orange-600 text-white font-bold py-2 px-4 rounded-full transition duration-300">
                      Contribute
                    </button>
                  </div>
                ))}
              </div>
            </div>

            {/* JHS Support Options */}
            <div className="bg-white p-8 rounded-lg shadow-lg">
              <h3 className="text-3xl font-bold mb-6 text-center text-orange-700">Junior High School (JHS)</h3>
              <p className="mb-6 text-center text-lg text-orange-600">Parents can support extra classes for JHS students:</p>
              <div className="text-center">
                <p className="text-xl mb-8 text-orange-700">Enroll your child in our problem-solving training program</p>
                <button className="bg-orange-500 hover:bg-orange-600 text-white font-bold py-3 px-6 rounded-full inline-flex items-center text-lg transition duration-300">
                  Register for Extra Classes <School className="ml-2" />
                </button>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Post-Training Support */}
      <section className="py-20 bg-white">
        <div className="container mx-auto px-4">
          <h2 className="text-4xl font-bold mb-12 text-center text-orange-800">Scaling Up Success</h2>
          <div className="flex flex-col md:flex-row items-center justify-between gap-12">
            <div className="md:w-1/2">
              <h3 className="text-3xl font-semibold mb-6 text-orange-700">Expert Support for School-Wide Implementation</h3>
              <p className="mb-6 text-lg text-orange-900">After the initial training, we don't just leave schools on their own. Our team provides ongoing expert support to help schools replicate successful projects on a larger scale, benefiting the entire school community.</p>
              <ul className="space-y-3">
                {[
                  "Technical expertise for scaling up projects",
                  "Guidance on sourcing materials and resources",
                  "Workshops for teachers to integrate projects into curriculum",
                  "Ongoing mentorship for student leaders"
                ].map((item, index) => (
                  <li key={index} className="flex items-center text-orange-700">
                    <ChevronRight className="mr-2 text-orange-500" />
                    {item}
                  </li>
                ))}
              </ul>
            </div>
            <div className="md:w-1/2">
              <img src="/api/placeholder/500/300" alt="Experts working with students" className="rounded-lg shadow-lg transform hover:scale-105 transition duration-300" />
            </div>
          </div>
        </div>
      </section>

      {/* Community Impact */}
      <section className="py-20 bg-orange-100">
        <div className="container mx-auto px-4">
          <h2 className="text-4xl font-bold mb-12 text-center text-orange-800">Community and Global Outreach</h2>
          <div className="flex flex-col md:flex-row items-center justify-center gap-12">
            <div className="md:w-1/2">
              <img src="/api/placeholder/500/300" alt="Community impact" className="rounded-lg shadow-lg transform hover:scale-105 transition duration-300" />
            </div>
            <div className="md:w-1/2">
              <h3 className="text-3xl font-semibold mb-6 text-orange-700">Expanding Our Reach</h3>
              <p className="mb-8 text-lg text-orange-900">We're offering training to selected deprived and local communities for free, enabling youth to build solutions tailored to their community's needs.</p>
              <button className="bg-orange-500 hover:bg-orange-600 text-white font-bold py-3 px-6 rounded-full inline-flex items-center text-lg transition duration-300">
                Donate Now <DollarSign className="ml-2" />
              </button>
            </div>
          </div>
        </div>
      </section>

      {/* Innovation Quiz */}
      <section className="py-20 bg-white">
        <div className="container mx-auto px-4 text-center">
          <h2 className="text-4xl font-bold mb-8 text-orange-800">Annual Innovation and Problem-Solving Quiz</h2>
          <p className="text-xl mb-12 text-orange-700">Join our exciting competition where schools tackle real industry challenges and showcase their innovative solutions!</p>
          <button className="bg-orange-500 hover:bg-orange-600 text-white font-bold py-3 px-6 rounded-full inline-flex items-center text-lg transition duration-300">
            Learn More <ChevronDown className="ml-2" />
          </button>
        </div>
      </section>

      {/* Footer */}
      <footer className="bg-orange-900 text-white py-12">
        <div className="container mx-auto px-4 text-center">
          <p className="text-lg">&copy; 2024 STEM Education Initiative. All rights reserved.</p>
        </div>
      </footer>
    </div>
  );
};

export default LandingPage;
