import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { School, Users, DollarSign, Link as LinkIcon, Copy } from 'lucide-react';

const ContributeSHS = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    contributorType: '',
    name: '',
    email: '',
    school: '',
    amount: '',
  });
  const [shareableLink, setShareableLink] = useState('');
  const [progress, setProgress] = useState(0);

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Here you would typically send the form data to your backend
    // For this example, we'll simulate a successful submission
    const campaignId = Math.random().toString(36).substring(7);
    const link = `https://yourdomain.com/contribute/${campaignId}`;
    setShareableLink(link);
    setProgress(10); // Start with 10% progress
  };

  const copyToClipboard = () => {
    navigator.clipboard.writeText(shareableLink);
    alert('Link copied to clipboard!');
  };

  return (
    <div className="min-h-screen bg-orange-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8 mx-auto">
        <div>
          <h2 className="mt-6 text-center text-3xl font-extrabold text-gray-900">
            Contribute to SHS Projects
          </h2>
          <p className="mt-2 text-center text-sm text-gray-600">
            Support education and innovation in Senior High Schools
          </p>
        </div>
        <form className="mt-8 space-y-6" onSubmit={handleSubmit}>
          <div className="rounded-md shadow-sm -space-y-px">
            <div>
              <label htmlFor="contributorType" className="sr-only">Contributor Type</label>
              <select
                id="contributorType"
                name="contributorType"
                required
                className="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-t-md focus:outline-none focus:ring-orange-500 focus:border-orange-500 focus:z-10 sm:text-sm"
                value={formData.contributorType}
                onChange={handleInputChange}
              >
                <option value="">Select Contributor Type</option>
                <option value="parent">Parent</option>
                <option value="school">School</option>
                <option value="oldStudent">Old Student</option>
              </select>
            </div>
            <div>
              <label htmlFor="name" className="sr-only">Name</label>
              <input
                id="name"
                name="name"
                type="text"
                required
                className="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 focus:outline-none focus:ring-orange-500 focus:border-orange-500 focus:z-10 sm:text-sm"
                placeholder="Name"
                value={formData.name}
                onChange={handleInputChange}
              />
            </div>
            <div>
              <label htmlFor="email" className="sr-only">Email address</label>
              <input
                id="email"
                name="email"
                type="email"
                autoComplete="email"
                required
                className="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 focus:outline-none focus:ring-orange-500 focus:border-orange-500 focus:z-10 sm:text-sm"
                placeholder="Email address"
                value={formData.email}
                onChange={handleInputChange}
              />
            </div>
            <div>
              <label htmlFor="school" className="sr-only">School Name</label>
              <input
                id="school"
                name="school"
                type="text"
                required
                className="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 focus:outline-none focus:ring-orange-500 focus:border-orange-500 focus:z-10 sm:text-sm"
                placeholder="School Name"
                value={formData.school}
                onChange={handleInputChange}
              />
            </div>
            <div>
              <label htmlFor="amount" className="sr-only">Contribution Amount</label>
              <select
                id="amount"
                name="amount"
                required
                className="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-b-md focus:outline-none focus:ring-orange-500 focus:border-orange-500 focus:z-10 sm:text-sm"
                value={formData.amount}
                onChange={handleInputChange}
              >
                <option value="">Select Contribution Amount</option>
                <option value="15000">GHC 15,000 (1 project)</option>
                <option value="25000">GHC 25,000 (2 projects)</option>
                <option value="40000">GHC 40,000 (3 projects)</option>
                <option value="50000">GHC 50,000 (5 projects)</option>
              </select>
            </div>
          </div>

          <div>
            <button
              type="submit"
              className="group relative w-full flex justify-center py-2 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-orange-600 hover:bg-orange-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-orange-500"
            >
              <span className="absolute left-0 inset-y-0 flex items-center pl-3">
                <DollarSign className="h-5 w-5 text-orange-500 group-hover:text-orange-400" aria-hidden="true" />
              </span>
              Contribute
            </button>
          </div>
        </form>

        {shareableLink && (
          <div className="mt-8 space-y-4">
            <h3 className="text-lg font-medium text-gray-900">Share your campaign</h3>
            <div className="flex items-center space-x-2">
              <input
                type="text"
                readOnly
                value={shareableLink}
                className="flex-1 p-2 border rounded-md"
              />
              <button
                onClick={copyToClipboard}
                className="p-2 bg-orange-100 rounded-md hover:bg-orange-200"
              >
                <Copy className="h-5 w-5 text-orange-600" />
              </button>
            </div>
            <div>
              <h4 className="text-sm font-medium text-gray-700 mb-1">Campaign Progress</h4>
              <div className="w-full bg-gray-200 rounded-full h-2.5">
                <div
                  className="bg-orange-600 h-2.5 rounded-full"
                  style={{ width: `${progress}%` }}
                ></div>
              </div>
              <p className="text-sm text-gray-600 mt-1">{progress}% of goal reached</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ContributeSHS;
